mapper.select:
select * from (
    select t.*,count(q.id) as questionCount,count(tl.id) as likeCount,q.status from tag t
    left join question_tag qt on qt.tag_id = t.id
    left join question q on q.id = qt.question_id and q.status = 1
    left join tag_like tl on tl.tag_id = t.id


            and t.name regexp #{search}


    group by t.id
)tqt order by tqt.questionCount desc,tqt.likeCount desc limit #{offSize},#{size}
